import React from "react";
import { View , StyleSheet, Image, Text } from "react-native";
import { Divider } from "native-base";
import Logo from '../assets/Aadhar-Color.png';

import ReactNativePinView from 'react-native-pin-view';

function CreatePin( props ) {
    return(
        <View style={ styles.createPinConatiner}>
            <View style={ styles.logoContainer}>
                <Image source={Logo} style={styles.logo} />
            </View>
            <View style={{ marginLeft: 20 , marginRight: 20 }}>
                <Divider orientation="horizontal" mx="auto" bg="coolGray.400" thickness="1"/>
            </View>
            <Text style={{ fontSize: 17 , textAlign: 'center' , marginTop: 5 , color: 'white'}}> SET UP YOUR MPIN </Text>
            <View style={styles.formContainer}>
            <ReactNativePinView
            inputSize={32}
            pinLength={8}
            buttonSize={60}
            // onValueChange={value => setEnteredPin(value)}
            buttonAreaStyle={{
              marginTop: 24,
            }}
            inputAreaStyle={{
              marginBottom: 24,
            }}
            inputViewEmptyStyle={{
              backgroundColor: "transparent",
              borderWidth: 1,
              borderColor: "#FFF",
            }}
            inputViewFilledStyle={{
              backgroundColor: "#FFF",
            }}
            buttonViewStyle={{
              borderWidth: 1,
              borderColor: "#FFF",
            }}
            buttonTextStyle={{
              color: "#FFF",
            }}
            // onButtonPress={key => {
            //   if (key === "custom_left") {
            //     pinView.current.clear()
            //   }
            //   if (key === "custom_right") {
            //     alert("Entered Pin: " + enteredPin)
            //   }
            //   if (key === "three") {
            //     alert("You can't use 3")
            //   }
            // }}
            // customLeftButton={showRemoveButton ? <Icon name={"ios-backspace"} size={36} color={"#FFF"} /> : undefined}
            // customRightButton={showCompletedButton ? <Icon name={"ios-unlock"} size={36} color={"#FFF"} /> : undefined}
          />
            </View>
        </View>
    )
}
export default CreatePin;


const styles = StyleSheet.create({
    createPinConatiner: {
        flex: 1,
        flexDirection: 'column',
        backgroundColor: '#27272a'
    }, 
    logoContainer: {
        flex: 1,
        justifyContent: 'center'
    },
    formContainer: {
        flex: 3, 
        marginRight:20,
        marginLeft:20,
        padding: 20,
    },
    logo : {
        width: 190,
        height: 120,
        alignSelf:'center',
    }
})
