import AsyncStorage from '@react-native-async-storage/async-storage';
const initialStateLocalStorage = {
    tokenFound: false
};

const LocalStorageReducer = async (state = initialStateLocalStorage , action) => {
    switch( action.type ) {
        case 'CHECK_USERTOKEN' : 
            try {
                const value = await AsyncStorage.getItem('userToken');
                if(value !== null) {
                    console.log('No Token Found')
                    return {
                        ...state,
                        tokenFound: true
                    }
                } 
            } catch(err) {
                console.log(err);
            }
            return { ...state }
        default: 
            return {
                ...state
            }
    }
};
export default LocalStorageReducer;
