import React , { useState } from "react";
import { View , Image , StyleSheet, Text } from "react-native";
import Logo from '../assets/Aadhar-Color.png';
import { Divider , Button , Input ,useToast } from "native-base";

import { connect } from 'react-redux';
import AsyncStorage from "@react-native-async-storage/async-storage";

function RegistrationScreen( props ) {

    const [aadharNo , setAadharNo] = useState('');
    const [isLoading , setIsLoading] = useState(false);

    const toast = useToast();

    const handleSubmit = () => {
        setIsLoading(true);
        setTimeout(() => {
            if (aadharNo.length === 12) {
                props.userRegister(aadharNo);
                props.navigation.navigate('CreatePIN');
            } else {
                console.log('Aadhar Number is 12 Digit Long!');
                setIsLoading(false);

                toast.show({
                    title: "Wrong Input",
                    status: "error",
                    description: "Aadhar Number is 12 Digit Long!  ",
                    duration: 5000,
                    variant: "outline-light"
                })
            }
        } , 1000);
    }

    return(
        <View style={styles.container}>
            <View style={styles.logoContainer}>
                <Image source={Logo} style={styles.logo}/>
            </View>
            <View style={{ marginLeft: 20 , marginRight: 20 }}>
                <Divider orientation="horizontal" mx="auto" bg="coolGray.400" thickness="1"/>
            </View>
            <View style={styles.formContainer}>
                <Input 
                    variant="filled" 
                    placeholder="Enter 12 Digit Aadhar Number"
                    keyboardType="number-pad"
                    onChangeText={text => setAadharNo(text)}
                />
                <Button 
                    isLoading={isLoading} 
                    isLoadingText="Submitting" 
                    style={{marginTop: 15 , marginLeft: 15 , marginRight: 15}}
                    onPress={() => handleSubmit()}
                >
                    Submit
                </Button>
            </View>
        </View>
    )
}

const matchStateToProps = (state , ownProps) => {
    return {

    }
}

const matchDispatchToProps = ( dispatch ) => {
    return {
        userRegister: async(aadharNumber) => { 
            const userToken = '#dftg653';
            try {
                await AsyncStorage.setItem('userToken' , userToken);
                dispatch({ type: 'USER_REGISTER' , aadharData: aadharNumber });
            } catch(e) {
                console.log(e);
            }
        },
    }
}

export default connect(matchStateToProps , matchDispatchToProps)(RegistrationScreen);

const styles = StyleSheet.create({
    container: {
        flex: 1,
        flexDirection: 'column',
        marginTop: 40
    },
    logoContainer: {
        flex: 1,
        justifyContent: 'center'
    },
    formContainer: {
        flex: 3, 
        marginRight:20,
        marginLeft:20,
        padding: 20,
    },
    logo : {
        width: 190,
        height: 120,
        alignSelf:'center',
    }
})
