import React , { useState } from "react";
import { View , Image , StyleSheet, Text } from "react-native";
import Logo from '../assets/Aadhar-Color.png';

import { Divider , Button , Input , FormControl } from "native-base";


export default function RegistrationScreen() {

    const [aadharNo , setAadharNo] = useState('');
    const [isLoading , setIsLoading] = useState(false);

    const handleSubmit = ( props ) => {
        setIsLoading(true);
        setTimeout(() => {
            if (aadharNo.length === 12) {
                console.log('All Corect');
                //Api Call;
            } else {
                console.log('Aadhar Number is 12 Digit Long!');
                setIsLoading(false);
            }
        } , 3000);
    }

    return(
        <View style={styles.container}>
            <View style={styles.logoContainer}>
                <Image source={Logo} style={styles.logo}/>
            </View>
            <View style={{ marginLeft: 20 , marginRight: 20 }}>
                <Divider orientation="horizontal" mx="auto" bg="coolGray.400" thickness="1"/>
            </View>
            <View style={styles.formContainer}>
                <FormControl>
                    <Input 
                        variant="filled" 
                        placeholder="Enter 12 Digit Aadhar Number"
                        keyboardType="number-pad"
                        onChangeText={text => setAadharNo(text)}
                    />
                    <FormControl.ErrorMessage leftIcon={<WarningOutlineIcon size="xs" />}>
                        Aadhar Number is 12 Digit Long
                    </FormControl.ErrorMessage>
                    <Button 
                        isLoading={isLoading} 
                        isLoadingText="Submitting" 
                        style={{marginTop: 15 , marginLeft: 15 , marginRight: 15}}
                        onPress={() => handleSubmit()}
                    >
                        Submit
                    </Button>
                </FormControl>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        flexDirection: 'column',
    },
    logoContainer: {
        flex: 1,
        justifyContent: 'center'
    },
    formContainer: {
        flex: 3, 
        marginRight:20,
        marginLeft:20,
        padding: 20,
    },
    logo : {
        width: 190,
        height: 120,
        alignSelf:'center',
    }
})
