import React , { useEffect } from "react";
import { Image, View , StyleSheet , ActivityIndicator} from "react-native";
import { connect } from "react-redux";

import AsyncStorage from '@react-native-async-storage/async-storage';

import aadharLogo from '../assets/Aadhar-Color.png';

const DELAY_TIME = 5000;

function WelcomeScreen( props ) {

    useEffect(() => {
        props.checkForUserToken();
        setTimeout(() => {
            if ( !props.loginStatus )
                props.navigation.navigate(`Registration`);
            props.navigation.navigate(`CreatePIN`);
        } , DELAY_TIME);
    } , []);

    return(
        <View style={ styles.container }>
            <Image source={aadharLogo} style={ styles.logo }/>
            <ActivityIndicator size="large" color="black" style={{marginTop: 100}}/>
        </View>
    )
}

const mapStateToProps = ( state ) => {
    return {
        loginStatus: state.LocalStorageReducer.tokenFound
    }
}

const mapDispatchToProps = ( dispatch ) => {
    return {
        checkForUserToken: async() => { 
            try {
                const value = await AsyncStorage.getItem('userToken');
                if (value !== null)
                    dispatch({ type: 'TOKEN_FOUND'})   
            } catch(err) {
                console.log(err);
            }
        }
    }
}

export default connect(mapStateToProps , mapDispatchToProps)(WelcomeScreen);

const styles = StyleSheet.create({
    container: {
        paddingTop: 20,
        flex: 1,
        justifyContent: 'center'
    },
    logo: {
        width: 190,
        height: 120,
        alignSelf:'center',
    }
})
